Números perfeitos 
program numerosPerfeitos;

uses crt;

var
 ate, x, soma, i: integer;

begin
 clrscr;
 x := 0;
 writeln('Numeros perfeitos abaixo de');
 Readln(ate);
 repeat
  x := x + 1;
  soma := 0;
  for i := 1 to x - 1 do
  begin
 if x mod i = 0 then
 soma := soma + i;
  end;
  if soma = x then
  begin
 writeln(x);
  end;
 until (x > ate);
 writeln('Pressione qualquer tecla para finalizar…');
 readkey;
end.

Repetição 
Soma dos números pares.

 program Soma_Pares;
 uses crt;
 var
  superior, soma, num: integer;
 begin
  soma:=0;
  write ('Entre com o limite superior');
  readln (superior);
  num:=2;

  repeat
 soma:=soma+num;
 num:=num+2;
  until (num > superior);

  writeln('A soma dos números pares de 2 até ', superior,' é ', soma);
  readln;
 end.

